document.getElementById('submit-btn').onclick = () => {
    const checked = document.querySelectorAll('input[type="radio"]:checked');
    if(checked.length < 40) return alert(`‡∏ï‡∏≠‡∏ö‡πÉ‡∏´‡πâ‡∏Ñ‡∏£‡∏ö‡∏Å‡πà‡∏≠‡∏ô‡∏Ñ‡∏£‡∏±‡∏ö`);

    let scores = {};
    let counts = {};
    for (let key in tracksData) { 
        scores[key] = 0; 
        counts[key] = 0; 
    }

    questions.forEach((q, idx) => {
        const val = parseInt(document.querySelector(`input[name="q${idx+1}"]:checked`).value);
        q.t.forEach(trackKey => {
            scores[trackKey] += val;
            counts[trackKey]++;
        });
    });

    // ‡∏£‡∏∞‡∏ö‡∏ö‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡πÅ‡∏ö‡∏ö "‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡πÄ‡∏â‡∏•‡∏µ‡πà‡∏¢" ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏•‡∏î‡∏Ñ‡∏ß‡∏≤‡∏°‡∏•‡∏≥‡πÄ‡∏≠‡∏µ‡∏¢‡∏á
    let results = [];
    for (let key in scores) {
        let avg = counts[key] > 0 ? scores[key] / counts[key] : 0;
        results.push({ key: key, score: avg + (Math.random() * 0.05) });
    }

    results.sort((a, b) => b.score - a.score);
    showResult(tracksData[results[0].key]);
};

