document.getElementById('submit-btn').onclick = () => {
    const checked = document.querySelectorAll('input[type="radio"]:checked');
    if(checked.length < 40) return alert(`‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏ï‡∏≠‡∏ö‡πÉ‡∏´‡πâ‡∏Ñ‡∏£‡∏ö‡∏Ñ‡∏£‡∏±‡∏ö (‡∏Ç‡∏≤‡∏î‡∏≠‡∏µ‡∏Å ${40 - checked.length} ‡∏Ç‡πâ‡∏≠)`);

    // 1. ‡∏™‡∏∞‡∏™‡∏°‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡πÅ‡∏•‡∏∞‡∏ô‡∏±‡∏ö‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏Ç‡πâ‡∏≠‡∏Ç‡∏≠‡∏á‡πÅ‡∏ï‡πà‡∏•‡∏∞‡∏™‡∏≤‡∏¢
    let scores = {};
    let counts = {};
    for (let key in tracksData) { scores[key] = 0; counts[key] = 0; }

    questions.forEach((q, idx) => {
        const val = parseInt(document.querySelector(`input[name="q${idx+1}"]:checked`).value);
        q.t.forEach(trackKey => {
            scores[trackKey] += val;
            counts[trackKey]++;
        });
    });

    // 2. ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡πÄ‡∏õ‡πá‡∏ô‡πÄ‡∏õ‡∏≠‡∏£‡πå‡πÄ‡∏ã‡πá‡∏ô‡∏ï‡πå (Normalization) - ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏°‡∏¢‡∏∏‡∏ï‡∏¥‡∏ò‡∏£‡∏£‡∏° 100%
    let results = [];
    for (let key in tracksData) {
        if (counts[key] > 0) {
            let maxPossible = counts[key] * 5;
            let percentage = (scores[key] / maxPossible) * 100;
            // ‡πÄ‡∏û‡∏¥‡πà‡∏° Jitter ‡πÄ‡∏•‡πá‡∏Å‡∏ô‡πâ‡∏≠‡∏¢ (0.001) ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏õ‡πâ‡∏≠‡∏á‡∏Å‡∏±‡∏ô‡∏Å‡∏£‡∏ì‡∏µ‡πÄ‡∏õ‡∏≠‡∏£‡πå‡πÄ‡∏ã‡πá‡∏ô‡∏ï‡πå‡πÄ‡∏ó‡πà‡∏≤‡∏Å‡∏±‡∏ô‡πÄ‡∏õ‡πä‡∏∞‡πÅ‡∏•‡πâ‡∏ß‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå‡∏Ñ‡πâ‡∏≤‡∏á‡∏ó‡∏µ‡πà‡∏™‡∏≤‡∏¢‡πÄ‡∏î‡∏¥‡∏°
            results.push({ key: key, score: percentage + (Math.random() * 0.01) });
        }
    }

    // 3. ‡πÄ‡∏£‡∏µ‡∏¢‡∏á‡∏•‡∏≥‡∏î‡∏±‡∏ö‡∏™‡∏≤‡∏¢‡∏ó‡∏µ‡πà‡πÑ‡∏î‡πâ % ‡∏™‡∏π‡∏á‡∏™‡∏∏‡∏î
    results.sort((a, b) => b.score - a.score);
    showResult(tracksData[results[0].key]);
};

