// 3. ‡∏£‡∏∞‡∏ö‡∏ö‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡πÅ‡∏ö‡∏ö "‡πÄ‡∏â‡∏•‡∏µ‡πà‡∏¢‡∏Ñ‡∏ß‡∏≤‡∏°‡πÄ‡∏Ç‡πâ‡∏≤‡∏Å‡∏±‡∏ô" (‡∏à‡∏ö‡∏õ‡∏±‡∏ç‡∏´‡∏≤‡∏™‡∏≤‡∏¢‡∏•‡πá‡∏≠‡∏Ñ)
document.getElementById('submit-btn').onclick = () => {
    const checked = document.querySelectorAll('input[type="radio"]:checked');
    if(checked.length < 40) return alert(`‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏ï‡∏≠‡∏ö‡πÉ‡∏´‡πâ‡∏Ñ‡∏£‡∏ö‡∏ó‡∏∏‡∏Å‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏£‡∏±‡∏ö`);

    // 3.1 ‡∏™‡∏£‡∏∏‡∏õ‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡∏î‡∏¥‡∏ö‡∏£‡∏≤‡∏¢‡∏ó‡∏±‡∏Å‡∏©‡∏∞
    let s = { math:0, science:0, logic:0, lang:0, social:0, creative:0, active:0, detail:0 };
    checked.forEach(input => {
        const qIdx = parseInt(input.name.substring(1)) - 1;
        const keys = questions[qIdx % questions.length].k;
        keys.forEach(k => { s[k] += parseInt(input.value); });
    });

    // 3.2 ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡∏´‡∏≤ "‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡πÄ‡∏â‡∏•‡∏µ‡πà‡∏¢" ‡∏Ç‡∏≠‡∏á‡πÅ‡∏ï‡πà‡∏•‡∏∞‡∏™‡∏≤‡∏¢‡∏à‡∏≤‡∏Å‡∏ó‡∏±‡∏Å‡∏©‡∏∞‡∏ó‡∏µ‡πà‡πÄ‡∏Å‡∏µ‡πà‡∏¢‡∏ß‡∏Ç‡πâ‡∏≠‡∏á
    let highestMatch = -1;
    let finalTrackKey = '';

    for (let trackKey in tracksData) {
        let track = tracksData[trackKey];
        let totalSkillScore = 0;
        
        // ‡∏ö‡∏ß‡∏Å‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡∏ó‡∏±‡∏Å‡∏©‡∏∞‡∏ó‡∏µ‡πà‡∏™‡∏≤‡∏¢‡∏ô‡∏µ‡πâ‡∏£‡∏∞‡∏ö‡∏∏‡πÑ‡∏ß‡πâ
        track.keys.forEach(k => { totalSkillScore += s[k]; });

        // ‡∏´‡∏≤‡∏Ñ‡πà‡∏≤‡πÄ‡∏â‡∏•‡∏µ‡πà‡∏¢ (‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡∏£‡∏ß‡∏° / ‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏ó‡∏±‡∏Å‡∏©‡∏∞) 
        // ‡∏à‡∏∏‡∏î‡∏ô‡∏µ‡πâ‡∏™‡∏≥‡∏Ñ‡∏±‡∏ç‡∏°‡∏≤‡∏Å! ‡πÄ‡∏û‡∏£‡∏≤‡∏∞‡∏à‡∏∞‡∏ó‡∏≥‡πÉ‡∏´‡πâ‡∏™‡∏≤‡∏¢‡∏ó‡∏µ‡πà‡∏°‡∏µ 2 ‡∏ó‡∏±‡∏Å‡∏©‡∏∞ ‡∏Å‡∏±‡∏ö 3 ‡∏ó‡∏±‡∏Å‡∏©‡∏∞ ‡∏°‡∏µ‡∏Ñ‡∏ß‡∏≤‡∏°‡∏™‡∏≥‡∏Ñ‡∏±‡∏ç‡πÄ‡∏ó‡πà‡∏≤‡∏Å‡∏±‡∏ô
        let averageMatch = totalSkillScore / track.keys.length;

        // ‡πÄ‡∏û‡∏¥‡πà‡∏° Jitter (‡πÄ‡∏•‡∏Ç‡∏™‡∏∏‡πà‡∏°‡∏ô‡πâ‡∏≠‡∏¢‡∏°‡∏≤‡∏Å) ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏õ‡πâ‡∏≠‡∏á‡∏Å‡∏±‡∏ô‡∏Å‡∏£‡∏ì‡∏µ‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡πÄ‡∏ó‡πà‡∏≤‡∏Å‡∏±‡∏ô‡πÄ‡∏õ‡πä‡∏∞‡πÅ‡∏•‡πâ‡∏ß‡∏£‡∏∞‡∏ö‡∏ö‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏™‡∏≤‡∏¢‡πÄ‡∏î‡∏¥‡∏°‡∏ã‡πâ‡∏≥‡πÜ
        let scoreWithJitter = averageMatch + (Math.random() * 0.01);

        if (scoreWithJitter > highestMatch) {
            highestMatch = scoreWithJitter;
            finalTrackKey = trackKey;
        }
    }

    showResult(tracksData[finalTrackKey]);
};

